import React, { useState } from 'react'
import styled from 'styled-components'
import { NavLink } from 'react-router-dom'
import { RefreshCw, Droplet, BarChart2, Twitter, Send, DollarSign } from 'react-feather'
import { useTranslation } from 'react-i18next'

const Nav = styled.nav`
  display: flex;
  justify-content: center;
  width: 100%;
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 99;
  background: ${({ theme }) => theme.bg1};
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  padding: 0;

  @media (max-width: 768px) {
    display: none;
  }
`

const NavList = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 1rem;
  max-width: 700px;
  margin: 0 auto;
  width: 100%;
`

const StyledNavLink = styled(NavLink)`
  display: flex;
  align-items: center;
  padding: 0.5rem 1rem;
  color: ${({ theme }) => theme.text1};
  text-decoration: none;
  border-radius: 12px;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${({ theme }) => theme.bg3};
  }

  &.active {
    background-color: ${({ theme }) => theme.bg3};
    color: ${({ theme }) => theme.primary1};
  }

  svg {
    width: 20px;
    height: 20px;
    margin-right: 8px;
  }
`

const ComingSoonToast = styled.div`
  position: fixed;
  bottom: 80px;
  left: 50%;
  transform: translateX(-50%);
  background: ${({ theme }) => theme.bg1};
  color: ${({ theme }) => theme.text1};
  padding: 1rem 2rem;
  border-radius: 12px;
  font-weight: 500;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.2);
  z-index: 1000;
`

const StyledButton = styled.button`
  display: flex;
  align-items: center;
  padding: 0.5rem 1rem;
  color: ${({ theme }) => theme.text1};
  text-decoration: none;
  border-radius: 12px;
  font-weight: 500;
  transition: all 0.2s ease;
  background: none;
  border: none;
  cursor: pointer;

  &:hover {
    background-color: ${({ theme }) => theme.bg3};
  }

  svg {
    width: 20px;
    height: 20px;
    margin-right: 8px;
  }
`

const ExternalLink = styled.a`
  display: flex;
  align-items: center;
  padding: 0.5rem 1rem;
  color: ${({ theme }) => theme.text1};
  text-decoration: none;
  border-radius: 12px;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${({ theme }) => theme.bg3};
  }

  svg {
    width: 20px;
    height: 20px;
    margin-right: 8px;
  }
`

export default function Navigation() {
  const { t } = useTranslation()
  const [showToast, setShowToast] = useState(false)

  const handleEarnClick = () => {
    setShowToast(true)
    setTimeout(() => setShowToast(false), 2000)
  }

  return (
    <Nav>
      <NavList>
        <StyledNavLink to="/swap" activeClassName="active">
          <RefreshCw />
          Swap
        </StyledNavLink>
        <StyledNavLink to="/pool" activeClassName="active">
          <Droplet />
          Pool
        </StyledNavLink>
        <StyledButton onClick={handleEarnClick}>
          <DollarSign />
          Earn
        </StyledButton>
        {showToast && <ComingSoonToast>Coming soon</ComingSoonToast>}
        <ExternalLink href="https://geckoterminal.com" target="_blank" rel="noopener noreferrer">
          <BarChart2 />
          {t('Chart')}
        </ExternalLink>
        <ExternalLink href="https://x.com/fomo_swap" target="_blank" rel="noopener noreferrer">
          <Twitter />
          Twitter
        </ExternalLink>
        <ExternalLink href="https://t.me/fomo_swap" target="_blank" rel="noopener noreferrer">
          <Send />
          Telegram
        </ExternalLink>
      </NavList>
    </Nav>
  )
}
